package com.example.tina;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import java.util.ArrayList;
import java.util.List;

public class Bebidas extends Fragment {

    private RecyclerView recyclerView;
    private CardapioAdapter cardapioAdapter;
    private List<MenuItem> bebidasList;

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View rootView = inflater.inflate(R.layout.fragment_bebidas, container, false);

        recyclerView = rootView.findViewById(R.id.gridlayout); // Certifique-se de substituir pelo ID correto

        bebidasList = new ArrayList<>();
        bebidasList.add(new MenuItem("Bebida 1", "Descrição da Bebida 1", 5.99, "data:image/jpeg;base64,"));
        bebidasList.add(new MenuItem("Bebida 2", "Descrição da Bebida 2", 4.99, "data:image/jpeg;base64,"));

        cardapioAdapter = new CardapioAdapter(bebidasList);
        recyclerView.setAdapter(cardapioAdapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));

        Button buttonVoltarCardapio = rootView.findViewById(R.id.btn_voltar);
        buttonVoltarCardapio.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                openCardapio();
            }
        });

        return rootView;
    }

    private void openCardapio() {
        Cardapio cardapio = new Cardapio();
        requireActivity().getSupportFragmentManager().beginTransaction()
                .replace(R.id.fragment_container, cardapio)
                .addToBackStack(null)
                .commit();
    }
}
